const n=t=>{t.store("menuState",{isMenuOpened:!1}),t.data("menu",()=>({selected:null,breakpoint:1279,breakpointMobile:1023,toggleSelected(e,i){e.type==="mouseover"&&window.innerWidth<this.breakpoint||e.type==="click"&&window.innerWidth>this.breakpoint||(this.selected=this.selected!==i?i:null)},menuOut(e){e.type==="mouseleave"&&window.innerWidth<this.breakpoint||(this.selected=null)},desktopCloseMenu(){window.innerWidth>this.breakpointMobile&&(this.selected=null)}}))};export{n as default};
